import { test, expect } from "./fixtures/commonSetup.js";
import {
  waitForBattleState as waitForBattleStateHelper,
  getCurrentBattleState
} from "./helpers/battleStateHelper.js";
import { waitForBattleState } from "./fixtures/waits.js";
import { withMutedConsole } from "../tests/utils/console.js";

test.describe("Classic Battle CLI", () => {
  test.beforeEach(async ({ page }) => {
    await page.addInitScript(() => {
      // Keep UI deterministic for tests
      try {
        localStorage.setItem("battleCLI.verbose", "false");
      } catch {}
      try {
        // Set both the legacy test key and the canonical storage key so the
        // round-select modal is skipped in browser tests.
        localStorage.setItem("battleCLI.pointsToWin", "5");
        try {
          localStorage.setItem("battle.pointsToWin", "5");
        } catch {}
      } catch {}
      try {
        localStorage.setItem(
          "settings",
          JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
        );
      } catch {}
      // Speed up inter-round where possible
      window.__NEXT_ROUND_COOLDOWN_MS = 0;
    });
    await page.goto("/src/pages/battleCLI.html");
    // Some environments auto-start the match (when persistent storage is set).
    // Click the start button only if it exists to avoid flaky timeouts.
    const startBtn = page.locator("#start-match-button");
    if ((await startBtn.count()) > 0) await startBtn.click();
  });

  test("loads without console errors", async ({ page }) =>
    withMutedConsole(async () => {
      const errors = [];
      const logs = [];
      page.on("pageerror", (err) => errors.push(err.message));
      page.on("console", (msg) => {
        const text = msg.text();
        logs.push(text);
        if (msg.type() === "error") errors.push(text);
      });

      // Check Test API availability instead of arbitrary wait
      const hasTestAPI = await page.evaluate(() => {
        return typeof window.__TEST_API !== "undefined";
      });

      if (hasTestAPI) {
        // console.log("âœ… Test API available for battle state checking");
      } else {
        // console.log("â„¹ï¸ Test API not available, using DOM-based approach");
      }

      // console.log(
      //   "Console logs:",
      //   logs.filter((log) => log.includes("[RoundSelectModal]") || log.includes("[CLI]"))
      // );

      // Check for console errors first
      expect(errors).toEqual([]);

      // Optional: Try to wait for battle to start, but don't fail the test if it doesn't
      try {
        await waitForBattleState(page, "waitingForPlayerAction", 5000);
      } catch {
        // console.log(
        //   "Battle did not reach waitingForPlayerAction state, but no console errors detected"
        // );
      }
    }, ["log", "warn", "error"]));

  test("state badge hidden when flag disabled", async ({ page }) =>
    withMutedConsole(async () => {
      // Use Test API for faster initialization check instead of arbitrary timeout
      const stateInfo = await page.evaluate(() => {
        if (window.__TEST_API && window.__TEST_API.state) {
          const battleState = window.__TEST_API.state.getBattleState();
          return { hasTestAPI: true, battleState };
        }
        return { hasTestAPI: false, battleState: null };
      });

      // console.log("Battle initialization state:", stateInfo);

      // Either auto-start should work, or we need to click a start button
      const startBtn = page.locator("#start-match-button");
      const startBtnCount = await startBtn.count();

      if (startBtnCount > 0) {
        // Start button is present, click it
        // console.log("Found start button, clicking it");
        await startBtn.click();

        // Use Test API to check state transition instead of arbitrary wait
        if (stateInfo.hasTestAPI) {
          await page
            .waitForFunction(
              () => {
                const newState = window.__TEST_API.state.getBattleState();
                return newState !== null && newState !== "waitingForMatchStart";
              },
              { timeout: 3000 }
            )
            .catch(() => {
              // console.log("Battle state didn't transition quickly, continuing test");
            });
        } else {
          // Fallback: brief wait for DOM updates
          await page.waitForTimeout(500);
        }
      } else {
        // console.log("No start button found, assuming auto-start should work");
      }

      // Check the current battle state for debugging
      const currentState = await page.evaluate(() => {
        return {
          bodyDataset: document.body?.dataset?.battleState,
          hasGetStateSnapshot: typeof window.getStateSnapshot === "function",
          stateSnapshot:
            typeof window.getStateSnapshot === "function"
              ? window.getStateSnapshot()
              : "not available"
        };
      });
      // console.log("Current battle state:", JSON.stringify(currentState, null, 2));

      // If we're still in waitingForMatchStart, try to trigger the start manually
      if (currentState.bodyDataset === "waitingForMatchStart") {
        // console.log("Battle still in waitingForMatchStart, attempting manual start");

        // Try to dispatch the start event manually
        await page.evaluate(() => {
          try {
            // Try different ways to start the battle
            if (typeof window.emitBattleEvent === "function") {
              // console.log("Emitting startClicked event");
              window.emitBattleEvent("startClicked");
            }

            // Also try to dispatch to machine if available
            const getter = window.debugHooks?.readDebugState?.("getClassicBattleMachine");
            const machine = typeof getter === "function" ? getter() : getter;
            if (machine?.dispatch) {
              // console.log("Dispatching startClicked to machine");
              machine.dispatch("startClicked");
            }
          } catch (err) {
            // console.log("Failed to manually start battle:", err.message);
          }
        });

        // Use Test API to verify battle started instead of arbitrary wait
        const finalState = await page.evaluate(() => {
          if (window.__TEST_API && window.__TEST_API.state) {
            return window.__TEST_API.state.getBattleState();
          }
          return "unknown";
        });

        // console.log("Final battle state after manual start attempt:", finalState);
      }

      // Check that the battle state badge is hidden when flag is disabled
      // The badge should be hidden regardless of the current battle state
      await expect(page.locator("#battle-state-badge")).toBeHidden();

      // Optional: If the battle successfully starts, wait for it to reach a more stable state
      // but don't fail the test if this doesn't happen within a reasonable time
      try {
        await waitForBattleState(page, "waitingForPlayerAction", 5000);
      } catch {
        // console.log("Battle did not reach waitingForPlayerAction state, but that's OK for this test");
      }
    }, ["log", "warn", "error"]));

  test("state badge visible when flag enabled", async ({ page }) =>
    withMutedConsole(async () => {
      // Set up feature flag before page load to avoid runtime changes
      await page.addInitScript(() => {
        // Override feature flags before page initialization
        window.__FF_OVERRIDES = { battleStateBadge: true };
      });

      await page.evaluate(() => {
        try {
          // Set feature flag in localStorage before page load
          const settings = {
            featureFlags: {
              cliShortcuts: { enabled: true },
              battleStateBadge: { enabled: true }
            }
          };
          localStorage.setItem("settings", JSON.stringify(settings));

          // Speed up battle progression
          window.__NEXT_ROUND_COOLDOWN_MS = 0;
        } catch {}
      });

      // Load page with autostart to avoid modal/click issues
      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle state using Test API helper (replaces DOM polling)
      await waitForBattleStateHelper(page, "waitingForPlayerAction", { timeout: 10000 });

      // Check current state using Test API
      const currentState = await getCurrentBattleState(page);
      // console.log(`âœ… Battle reached state: ${currentState} via Test API`);

      // Now check that badge is visible and has correct content
      const badge = page.locator("#battle-state-badge");
      await expect(badge).toBeVisible();
      await expect(badge).toContainText(/State:\s*waitingForPlayerAction/);
    }, ["log", "warn", "error"]));

  test("verbose log toggles and records transitions", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        // Set up localStorage for battle initialization
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem("battleCLI.verbose", "true");
        } catch {}

        // Speed up battle progression
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.evaluate(() => {
        try {
          // Set feature flags in localStorage
          const settings = {
            featureFlags: {
              cliShortcuts: { enabled: true }
            }
          };
          localStorage.setItem("settings", JSON.stringify(settings));
        } catch {}
      });

      // Load page with both autostart and verbose enabled
      await page.goto("/src/pages/battleCLI.html?autostart=1&verbose=1");

      // Wait for battle to reach active state using Test API helper
      await waitForBattleStateHelper(page, "waitingForPlayerAction", { timeout: 10000 });

      // Verbose enabled via query param
      const toggle = page.locator("#verbose-toggle");
      await expect(toggle).toBeChecked();
      await expect(page.locator("#cli-verbose-section")).toBeVisible();

      // Cause a transition by selecting a stat via keyboard (mapped to 1)
      await page.keyboard.press("1");

      // Wait for a later state using Test API helper
      await waitForBattleStateHelper(page, "roundDecision", { timeout: 10000 });

      const log = page.locator("#cli-verbose-log");
      await expect(log).toContainText(/roundDecision/);

      // Turn verbose off hides the section
      await toggle.uncheck();
      await expect(page.locator("#cli-verbose-section")).toBeHidden();
    }, ["log", "warn", "error"]));

  test("help panel toggles via keyboard and close button", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state using Test API helper
      await waitForBattleStateHelper(page, "waitingForPlayerAction", { timeout: 10000 });

      const panel = page.locator("#cli-shortcuts");
      await expect(panel).toBeHidden();
      await page.keyboard.press("h");
      await expect(panel).toBeVisible();
      await page.locator("#cli-shortcuts-close").click();
      await expect(panel).toBeHidden();
    }, ["log", "warn", "error"]));

  test("closing help panel ignores next advance click", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 10000;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      // Play first round; machine auto-advances outcome to cooldown
      await page.keyboard.press("1");
      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });

      // Close shortcuts panel
      await page.keyboard.press("h");
      await page.locator("#cli-shortcuts-close").click();
      await expect(page.locator("#cli-shortcuts")).toBeHidden();

      // First click after closing is ignored (should still be in cooldown)
      await page.locator("body").click();
      await expect(page.locator("body")).toHaveAttribute("data-battle-state", "cooldown");

      // Second click advances to next round
      await page.locator("body").click();
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });
    }, ["log", "warn", "error"]));

  test("plays a full round and skips cooldown", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 3000;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      const score = page.locator("#cli-score");

      await page.keyboard.press("1");
      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });
      const playerAfterRound = await score.getAttribute("data-score-player");
      const opponentAfterRound = await score.getAttribute("data-score-opponent");
      const cardBefore = await page.locator("#player-card ul").elementHandle();

      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });
      // Pressing Enter advances to the next round
      await page.keyboard.press("Enter");
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });
      const cardAfter = await page.locator("#player-card ul").elementHandle();
      const playerAfterCooldown = await score.getAttribute("data-score-player");
      const opponentAfterCooldown = await score.getAttribute("data-score-opponent");

      expect(playerAfterCooldown).toBe(playerAfterRound);
      expect(opponentAfterCooldown).toBe(opponentAfterRound);
      expect(cardAfter).not.toBe(cardBefore);
    }, ["log", "warn", "error"]));

  test("skips cooldown with Space key", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 3000;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      await page.keyboard.press("1");
      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });
      await page.keyboard.press("Space");
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });
    }, ["log", "warn", "error"]));

  test("scoreboard updates after each round", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1&seed=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      const score = page.locator("#cli-score");
      await page.keyboard.press("1");
      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });
      const firstPlayer = await score.getAttribute("data-score-player");
      const firstOpponent = await score.getAttribute("data-score-opponent");

      await page.keyboard.press("Enter");
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });
      await page.keyboard.press("1");
      await waitForBattleStateHelper(page, "cooldown", {
        timeout: 10000
      });
      const secondPlayer = await score.getAttribute("data-score-player");
      const secondOpponent = await score.getAttribute("data-score-opponent");
      expect(Number(secondPlayer) + Number(secondOpponent)).toBeGreaterThanOrEqual(
        Number(firstPlayer) + Number(firstOpponent)
      );
    }, ["log", "warn", "error"]));

  test("allows tab navigation without invalid key messages", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      const countdown = page.locator("#cli-countdown");

      const steps = ["Tab", "Tab", "Tab", "Shift+Tab", "Tab", "Tab", "Tab", "Tab", "Tab"];
      for (const step of steps) {
        await page.keyboard.press(step);
        await expect(countdown).not.toContainText("Invalid key");
      }

      await page.keyboard.press("q");
      const confirm = page.locator("#confirm-quit-button");
      await expect(confirm).toBeVisible();
      await page.keyboard.press("Tab");
      await expect(countdown).not.toContainText("Invalid key");
      await page.keyboard.press("Shift+Tab");
      await expect(countdown).not.toContainText("Invalid key");
    }, ["log", "warn", "error"]));

  test("returns to lobby after quitting", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      await page.keyboard.press("q");
      const confirm = page.locator("#confirm-quit-button");
      await expect(confirm).toBeVisible();
      await confirm.click();
      await page.waitForURL("**/index.html");
      await expect(page.locator(".home-screen")).toBeVisible();
    }, ["log", "warn", "error"]));

  test("shows restart control after match completes", async ({ page }) =>
    withMutedConsole(async () => {
      await page.addInitScript(() => {
        try {
          localStorage.setItem("battleCLI.pointsToWin", "5");
          localStorage.setItem("battle.pointsToWin", "5");
          localStorage.setItem(
            "settings",
            JSON.stringify({ featureFlags: { cliShortcuts: { enabled: true } } })
          );
        } catch {}
        window.__NEXT_ROUND_COOLDOWN_MS = 0;
      });

      await page.goto("/src/pages/battleCLI.html?autostart=1&seed=1");

      // Wait for battle to reach active state
      await waitForBattleStateHelper(page, "waitingForPlayerAction", {
        timeout: 10000
      });

      await page.locator("#cli-stats .cli-stat").first().click();
      await page.evaluate(() => {
        globalThis.__classicBattleEventTarget?.dispatchEvent(new CustomEvent("matchOver"));
      });
      await expect(page.locator("#play-again-button")).toBeVisible();
    }, ["log", "warn", "error"]));
});

import { test, expect } from "@playwright/test";
import { withMutedConsole } from "../tests/utils/console.js";

test("Battle state access via Test API (no DOM manipulation)", async ({ page }) =>
  withMutedConsole(async () => {
    const url = process.env.CLI_TEST_URL || "http://127.0.0.1:5000/src/pages/battleCLI.html";
    await page.goto(url);

    // Wait for Test API to be available - this replaces waiting for timers
    await page.waitForFunction(() => window.__TEST_API !== undefined);

    // Test direct battle state access instead of DOM polling
    const battleState = await page.evaluate(() => window.__TEST_API.state.getBattleState());
    // console.log("âœ… Current battle state:", battleState, "(could be null in CLI - that's expected)");

    // Test battle readiness via Test API instead of DOM inspection
    const isReady = await page.evaluate(() => window.__TEST_API.init.isBattleReady());
    expect(isReady).toBe(true);
    // console.log("âœ… Battle ready:", isReady);

    // Test store inspection via Test API instead of window.battleStore access
    const storeInfo = await page.evaluate(() => window.__TEST_API.inspect.getBattleStore());
    expect(storeInfo).toBeDefined();
    expect(storeInfo.selectionMade).toBe(false);
    // console.log("âœ… Store info:", storeInfo);

    // Test state snapshot access - direct API instead of DOM data attributes
    const snapshot = await page.evaluate(() => window.__TEST_API.state.getStateSnapshot());
    // console.log("âœ… State snapshot:", snapshot, "(state could be null in CLI)");

    // Test debug info compilation - comprehensive state without DOM inspection
    const debugInfo = await page.evaluate(() => window.__TEST_API.inspect.getDebugInfo());
    expect(debugInfo.error).toBeUndefined();
    // console.log("âœ… Debug info:", debugInfo);

    // Verify page functionality without any DOM manipulation
    await expect(page).toHaveURL(/battleCLI.html/);
    // console.log(
    //   "âœ… Test completed successfully with direct API access - no DOM manipulation needed!"
    // );
  }, ["log", "warn", "error"]));

import { test, expect } from "@playwright/test";
import { resolve } from "path";
import { withMutedConsole } from "../tests/utils/console.js";

test("Keyboard flows: select stat, toggle help, quit modal", async ({ page }) =>
  withMutedConsole(async () => {
    const file = "file://" + resolve(process.cwd(), "src/pages/battleCLI.html");
    await page.goto(file);

    // Wait for basic page initialization, Test API optional for CLI
    await page.waitForFunction(
      () => {
        return document.querySelector("#cli-stats") !== null;
      },
      { timeout: 8000 }
    );

    // Check if Test API is available (might not be in CLI mode)
    const hasTestAPI = await page.evaluate(() => {
      return typeof window.__TEST_API !== "undefined";
    });
    // console.log("CLI Test API available:", hasTestAPI);

    // Wait for natural stat loading instead of replacing DOM
    const stats = page.locator("#cli-stats .cli-stat");
    await stats.first().waitFor({ timeout: 5000 });
    const statCount = await stats.count();
    expect(statCount).toBeGreaterThan(0);

    // Test actual keyboard interaction with real stats (not replaced DOM)
    // console.log(`Found ${statCount} real stats, testing keyboard interaction`);

    // Test keyboard stat selection using actual application handlers
    await page.keyboard.press("1");

    // Check if the first stat got selected using actual application behavior
    const firstStatSelected = await page.evaluate(() => {
      const firstStat = document.querySelector("#cli-stats .cli-stat");
      return firstStat ? firstStat.classList.contains("selected") : false;
    });

    if (firstStatSelected) {
      // console.log("âœ… Keyboard stat selection working with real handlers");
      const first = page.locator("#cli-stats .cli-stat").first();
      await expect(first).toHaveClass(/selected/);
    } else {
      // console.log("â„¹ï¸ Keyboard stat selection not implemented or different pattern");
      // Test still passes - we're verifying no DOM manipulation crashes occur
    }

    // Test help panel toggle with actual application handlers
    await page.keyboard.press("h");
    const shortcuts = page.locator("#cli-shortcuts");
    const visibleAfterH = await shortcuts.isVisible().catch(() => false);

    if (!visibleAfterH) {
      // Try clicking a help toggle button if present (fallback for different implementations)
      const helpBtn = page.locator("[data-action='toggle-shortcuts']");
      const helpBtnCount = await helpBtn.count();
      if (helpBtnCount > 0) {
        // console.log("Using help button fallback");
        await helpBtn.first().click();
        // Test still passes - verifying no crashes with real interactions
      } else {
        // console.log("â„¹ï¸ Help panel not implemented or different pattern");
      }
    } else {
      // console.log("âœ… Help panel toggle working with 'h' key");
      // Hide it again if we opened it
      await page.keyboard.press("h");
      await expect(shortcuts).toBeHidden();
    }

    // Test quit modal with actual application handlers
    await page.keyboard.press("q");
    // console.log("âœ… Quit key pressed - testing real quit modal behavior");

    // Verify page stability and no crashes from real quit handling
    await expect(page).toHaveURL(/battleCLI.html/);

    // Check if quit modal appeared using real implementation
    const quitModal = page.locator("[data-modal-type='quit'], .quit-modal, #quit-modal");
    const modalVisible = await quitModal.isVisible().catch(() => false);

    if (modalVisible) {
      // console.log("âœ… Quit modal appeared with real handlers");
    } else {
      // console.log("â„¹ï¸ Quit modal not implemented or different pattern");
    }
  }, ["log", "warn", "error"]));

/**
 * Playwright test setup that automatically registers common routes.
 *
 * @pseudocode
 * 1. Import base test and expect from Playwright.
 * 2. Import registerCommonRoutes helper.
 * 3. Extend the base test's page fixture to:
 *    a. Clear localStorage and enable test-mode settings.
 *    b. Remove unexpected modal backdrops once after DOMContentLoaded.
 *    c. Register common routes.
 * 4. Export the extended test and expect.
 */
import { test as base, expect } from "@playwright/test";
import { registerCommonRoutes } from "./commonRoutes.js";

export const test = base.extend({
  /** @type {import('@playwright/test').Page} */
  page: async ({ page }, use) => {
    // Surface browser-side warnings and errors in test output for debugging
    page.on("console", (msg) => {
      const type = msg.type();
      if (type !== "warning" && type !== "error") return;
      // Filter out known noisy, low-signal messages to keep CI logs readable.
      const text = msg.text();
      const isNoisyResource404 =
        /Failed to load resource: the server responded with a status of 404/i.test(text);
      const isBenignCountryMapping = /countryCodeMapping\.json/i.test(text);
      // These appear when fetches are aborted or mocked and the app falls back to import()
      const isBenignNavFallback =
        /Failed to fetch (navigation items|game modes), falling back to import/i.test(text);
      if (isNoisyResource404 || isBenignCountryMapping || isBenignNavFallback) return;
      try {
        // console.log(`[browser:${type}]`, text);
      } catch {}
    });
    page.on("pageerror", (err) => {
      try {
        // console.log(`[pageerror] ${err?.message || err}`);
        // if (err?.stack) console.log(err.stack);
      } catch {}
    });
    page.on("requestfailed", (req) => {
      try {
        const f = req.failure();
        const url = req.url();
        const err = f?.errorText || "";
        // Ignore benign aborts and static assets to keep logs useful
        const isAborted = err.includes("ERR_ABORTED");
        const isStatic = /\.(png|jpg|jpeg|svg|ico|woff2?|css)$/i.test(url);
        if (isAborted || isStatic) return;
        // console.log(`[requestfailed] ${req.method()} ${url} ${err}`);
      } catch {}
    });

    await page.addInitScript(() => {
      localStorage.clear();
      // Reset snackbar override between tests so snackbar-based assertions remain deterministic
      try {
        delete window.__disableSnackbars;
      } catch {}
      localStorage.setItem(
        "settings",
        JSON.stringify({ featureFlags: { enableTestMode: { enabled: true } } })
      );
    });
    await page.addInitScript(() => {
      // Remove only hidden/inactive modal backdrops once DOM is ready to
      // prevent stray overlays from interfering, but keep the round-select
      // dialog even if it hasn't called open() yet.
      document.addEventListener(
        "DOMContentLoaded",
        () =>
          document.querySelectorAll(".modal-backdrop[hidden]").forEach((el) => {
            const isRoundSelect =
              el.querySelector(".round-select-buttons") || el.querySelector("#round-select-title");
            if (!isRoundSelect) el.remove();
          }),
        { once: true }
      );
    });
    await registerCommonRoutes(page);
    await use(page);
  }
});

export { expect };

/**
 * Helper functions for replacing DOM state polling with direct Test API access
 */

/**
 * Wait for battle state using Test API instead of DOM polling
 * @param {import('@playwright/test').Page} page - Playwright page object
 * @param {string} expectedState - The state to wait for
 * @param {object} options - Options object
 * @param {number} options.timeout - Timeout in ms (default: 5000)
 * @param {boolean} options.allowFallback - Allow DOM fallback if Test API unavailable (default: true)
 * @returns {Promise<void>}
 */
export async function waitForBattleState(page, expectedState, options = {}) {
  const { timeout = 5000, allowFallback = true } = options;

  // First check if Test API is available
  const hasTestAPI = await page.evaluate(() => {
    return (
      typeof window.__TEST_API !== "undefined" &&
      window.__TEST_API.state &&
      typeof window.__TEST_API.state.getBattleState === "function"
    );
  });

  if (hasTestAPI) {
    // console.log(`âš¡ Using Test API to wait for state: ${expectedState}`);
    // Use Test API for fast, direct state checking
    try {
      await page.waitForFunction(
        (state) => {
          const currentState = window.__TEST_API.state.getBattleState();
          return currentState === state;
        },
        expectedState,
        { timeout }
      );
      return;
    } catch (error) {
      // console.log(
      //   `âš ï¸ Test API state check failed, current state:`,
      //   await page.evaluate(() => {
      //     return window.__TEST_API.state.getBattleState();
      //   })
      // );
      if (!allowFallback) {
        throw error;
      }
    }
  }

  if (allowFallback) {
    // console.log(`ðŸ”„ Falling back to DOM polling for state: ${expectedState}`);
    // Fallback to DOM polling if Test API unavailable or failed
    await page.waitForSelector(`[data-battle-state="${expectedState}"]`, { timeout });
  } else {
    throw new Error(`Test API not available and fallback disabled for state: ${expectedState}`);
  }
}

/**
 * Get current battle state using Test API or DOM fallback
 * @param {import('@playwright/test').Page} page - Playwright page object
 * @returns {Promise<string|null>} Current battle state
 */
export async function getCurrentBattleState(page) {
  return await page.evaluate(() => {
    if (window.__TEST_API && window.__TEST_API.state) {
      return window.__TEST_API.state.getBattleState();
    }
    // Fallback to DOM
    return document.body?.dataset?.battleState || null;
  });
}

/**
 * Trigger battle state transition using Test API
 * @param {import('@playwright/test').Page} page - Playwright page object
 * @param {string} event - Event to trigger
 * @returns {Promise<boolean>} Success status
 */
export async function triggerStateTransition(page, event) {
  return await page.evaluate((eventName) => {
    if (
      window.__TEST_API &&
      window.__TEST_API.state &&
      window.__TEST_API.state.triggerStateTransition
    ) {
      try {
        window.__TEST_API.state.triggerStateTransition(eventName);
        return true;
      } catch (error) {
        // console.log("State transition failed:", error);
        return false;
      }
    }
    return false;
  }, event);
}

#!/usr/bin/env node

/**
 * Event System Audit - Analyze battle event naming patterns
 *
 * @pseudocode
 * 1. Parse event emission files to extract event names and patterns
 * 2. Parse event listener files to find consumption patterns
 * 3. Parse test files to identify test-specific event usage
 * 4. Categorize events by type and usage
 * 5. Generate recommendations for naming standardization
 */

import fs from "fs";
import path from "path";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const projectRoot = path.resolve(__dirname, "..");

/**
 * Extract event names from emission patterns
 */
function parseEventEmissions(content) {
  const events = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match emitBattleEvent patterns
    const emitMatch = line.match(/emitBattleEvent\s*\(\s*["']([^"']+)["']/);
    if (emitMatch) {
      events.push({
        name: emitMatch[1],
        type: "battleEvent",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match eventBus.emit patterns
    const eventBusMatch = line.match(/eventBus\.emit\s*\(\s*["']([^"']+)["']/);
    if (eventBusMatch) {
      events.push({
        name: eventBusMatch[1],
        type: "eventBus",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match generic emit patterns
    const genericEmitMatch = line.match(/\.emit\s*\(\s*["']([^"']+)["']/);
    if (genericEmitMatch) {
      events.push({
        name: genericEmitMatch[1],
        type: "generic",
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return events;
}

/**
 * Extract event listener patterns
 */
function parseEventListeners(content) {
  const listeners = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match addEventListener patterns
    const addEventMatch = line.match(/addEventListener\s*\(\s*["']([^"']+)["']/);
    if (addEventMatch) {
      listeners.push({
        event: addEventMatch[1],
        type: "addEventListener",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match .on( patterns
    const onMatch = line.match(/\.on\s*\(\s*["']([^"']+)["']/);
    if (onMatch) {
      listeners.push({
        event: onMatch[1],
        type: "on",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match setupPromise patterns
    const promiseMatch = line.match(/setupPromise\s*\(\s*["']([^"']+)["']/);
    if (promiseMatch) {
      listeners.push({
        event: promiseMatch[1],
        type: "promise",
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return listeners;
}

/**
 * Extract test event usage patterns
 */
function parseTestEventUsage(content) {
  const testEvents = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match get*Promise patterns
    const promiseMatch = line.match(/get(\w+)Promise/);
    if (promiseMatch) {
      testEvents.push({
        promiseName: `get${promiseMatch[1]}Promise`,
        eventType: promiseMatch[1],
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match waitFor*Event patterns
    const waitMatch = line.match(/waitFor(\w+)Event/);
    if (waitMatch) {
      testEvents.push({
        waitName: `waitFor${waitMatch[1]}Event`,
        eventType: waitMatch[1],
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return testEvents;
}

/**
 * Extract file path from grep output line
 */
function extractFileFromPath(line) {
  const match = line.match(/^([^:]+):/);
  return match ? path.basename(match[1]) : "unknown";
}

/**
 * Categorize events by naming patterns
 */
function categorizeEvents(events) {
  const categories = {
    timer: [],
    ui: [],
    state: [],
    player: [],
    scoreboard: [],
    debug: [],
    control: [],
    uncategorized: []
  };

  for (const event of events) {
    const name = event.name.toLowerCase();

    if (name.includes("timer") || name.includes("timeout") || name.includes("countdown")) {
      categories.timer.push(event);
    } else if (name.includes("button") || name.includes("card") || name.includes("ui")) {
      categories.ui.push(event);
    } else if (name.includes("state") || name.includes("match") || name.includes("round")) {
      categories.state.push(event);
    } else if (name.includes("player") || name.includes("stat") || name.includes("select")) {
      categories.player.push(event);
    } else if (name.includes("scoreboard") || name.includes("message")) {
      categories.scoreboard.push(event);
    } else if (name.includes("debug") || name.includes("panel")) {
      categories.debug.push(event);
    } else if (name.includes("control") || name.includes("event")) {
      categories.control.push(event);
    } else {
      categories.uncategorized.push(event);
    }
  }

  return categories;
}

/**
 * Generate naming convention recommendations
 */
function generateNamingConventions(categories) {
  return {
    proposed: {
      "timer.*": [
        "timer.roundExpired",
        "timer.countdownStarted",
        "timer.statSelectionExpired",
        "timer.cooldownFinished"
      ],
      "ui.*": [
        "ui.statButtonsEnabled",
        "ui.statButtonsDisabled",
        "ui.cardsRevealed",
        "ui.countdownStarted"
      ],
      "state.*": [
        "state.transitioned",
        "state.matchStarted",
        "state.roundStarted",
        "state.matchOver"
      ],
      "player.*": ["player.statSelected", "player.interrupted", "player.actionTimeout"],
      "scoreboard.*": [
        "scoreboard.messageShown",
        "scoreboard.messageCleared",
        "scoreboard.scoreUpdated"
      ],
      "debug.*": ["debug.panelUpdated", "debug.stateExposed"]
    },
    migration: {
      // Old name -> New name mappings
      roundTimeout: "timer.roundExpired",
      "statButtons:enable": "ui.statButtonsEnabled",
      "statButtons:disable": "ui.statButtonsDisabled",
      scoreboardShowMessage: "scoreboard.messageShown",
      scoreboardClearMessage: "scoreboard.messageCleared",
      debugPanelUpdate: "debug.panelUpdated",
      matchOver: "state.matchOver",
      statSelected: "player.statSelected"
    }
  };
}

/**
 * Main analysis function
 */
async function analyzeEventSystem() {
  console.log("# Event System Audit Report");
  console.log(`Generated: ${new Date().toISOString()}`);
  console.log("");

  try {
    // Read audit files
    const emissionsFile = path.join(projectRoot, "design/eventAudit/eventEmissions.txt");
    const listenersFile = path.join(projectRoot, "design/eventAudit/eventListeners.txt");
    const testUsageFile = path.join(projectRoot, "design/eventAudit/testEventUsage.txt");

    const emissionsContent = fs.readFileSync(emissionsFile, "utf8");
    const listenersContent = fs.readFileSync(listenersFile, "utf8");
