/**
 * RAG preflight checks for CI and local validation.
 *
 * Checks:
 * - Strict offline model presence when RAG_STRICT_OFFLINE=1
 * - Offline artifacts consistency: metadata vs binary vector file
 */
import path from "node:path";
import { fileURLToPath } from "node:url";
import { readFile, stat } from "node:fs/promises";

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const rootDir = path.resolve(__dirname, "..");
const srcDir = path.join(rootDir, "src");

const REQUIRED_MODEL_FILES = [
  "config.json",
  "tokenizer.json",
  "tokenizer_config.json",
  path.join("onnx", "model_quantized.onnx")
];

async function fileExistsNonEmpty(p) {
  try {
    const s = await stat(p);
    return s.size > 0;
  } catch {
    return false;
  }
}

export async function checkStrictOfflineModel(env = process.env) {
  const errors = [];
  const strict = env?.RAG_STRICT_OFFLINE === "1";
  if (!strict) return { ok: true, errors };
  const modelDir = path.join(srcDir, "models", "minilm");
  for (const rel of REQUIRED_MODEL_FILES) {
    const full = path.join(modelDir, rel);
    const ok = await fileExistsNonEmpty(full);
    if (!ok) errors.push(`Strict offline: missing model file ${path.relative(rootDir, full)}`);
  }
  return { ok: errors.length === 0, errors };
}

export async function checkOfflineArtifacts() {
  const errors = [];
  const metaPath = path.join(srcDir, "data", "offline_rag_metadata.json");
  const vecPath = path.join(srcDir, "data", "offline_rag_vectors.bin");
  try {
    const raw = await readFile(metaPath, "utf8");
    const meta = JSON.parse(raw);
    const vectorLength = Number(meta.vectorLength || 0);
    const count = Number(meta.count || 0);
    if (!vectorLength || !count) {
      errors.push("Offline artifacts: vectorLength or count missing/zero in metadata");
    } else {
      const buf = await readFile(vecPath);
      const expected = vectorLength * count; // Int8Array bytes
      if (buf.byteLength !== expected) {
        errors.push(
          `Offline artifacts: vector bin length ${buf.byteLength} != expected ${expected} (` +
            `${vectorLength} x ${count})`
        );
      }
    }
  } catch (err) {
    errors.push(
      `Offline artifacts: failed to read files (${path.relative(rootDir, metaPath)}, ${path.relative(
        rootDir,
        vecPath
      )})`
    );
  }
  return { ok: errors.length === 0, errors };
}

export async function checkPreflight() {
  const issues = [];
  const a = await checkStrictOfflineModel();
  if (!a.ok) issues.push(...a.errors);
  const b = await checkOfflineArtifacts();
  if (!b.ok) issues.push(...b.errors);
  return { ok: issues.length === 0, errors: issues };
}

if (process.argv[1] === fileURLToPath(import.meta.url)) {
  const res = await checkPreflight();
  if (!res.ok) {
    console.error("RAG preflight failed:\n- " + res.errors.join("\n- "));
    process.exit(1);
  }

  console.log("RAG preflight: OK");
}

#!/usr/bin/env node

/**
 * Validate test architecture patterns across the codebase
 * Checks for anti-patterns and suggests improvements
 */

import { readFileSync, readdirSync, statSync } from "fs";
import { join, extname } from "path";

const ANTI_PATTERNS = {
  manualDomForComplex: {
    pattern: /document\.body\.innerHTML.*=.*['"`].*(<.*>.*){3,}/,
    message: "Complex manual DOM detected - consider using real HTML for integration tests",
    severity: "warning"
  },
  missingCleanup: {
    pattern: /createRealHtmlTestEnvironment\(\)/,
    requiresPattern: /cleanup\(\)/,
    message: "Real HTML test environment created but cleanup() not found",
    severity: "error"
  },
  inconsistentNaming: {
    pattern: /describe\(['"`][^'"`]*['"`]/,
    message: "Consider using consistent naming: Component (Unit|DOM|Integration)",
    severity: "info"
  }
};

const GOOD_PATTERNS = {
  realHtmlUtils: /createRealHtmlTestEnvironment|validateRealHtmlStructure/,
  properCleanup: /afterEach.*cleanup/,
  clearNaming: /describe\(['"`][^'"`]*\s*\((Unit|DOM|Integration)\)/
};

function findTestFiles(dir, files = []) {
  const items = readdirSync(dir);

  for (const item of items) {
    const fullPath = join(dir, item);
    const stat = statSync(fullPath);

    if (stat.isDirectory() && !item.includes("node_modules")) {
      findTestFiles(fullPath, files);
    } else if (item.endsWith(".test.js") || item.endsWith(".spec.js")) {
      files.push(fullPath);
    }
  }

  return files;
}

function analyzeTestFile(filePath) {
  const content = readFileSync(filePath, "utf-8");
  const results = {
    file: filePath,
    issues: [],
    goodPatterns: [],
    score: 0
  };

  // Check for anti-patterns
  for (const [name, config] of Object.entries(ANTI_PATTERNS)) {
    if (config.pattern.test(content)) {
      if (config.requiresPattern && !config.requiresPattern.test(content)) {
        results.issues.push({
          type: name,
          message: config.message,
          severity: config.severity
        });
      } else if (!config.requiresPattern) {
        results.issues.push({
          type: name,
          message: config.message,
          severity: config.severity
        });
      }
    }
  }

  // Check for good patterns
  for (const [name, pattern] of Object.entries(GOOD_PATTERNS)) {
    if (pattern.test(content)) {
      results.goodPatterns.push(name);
      results.score += 1;
    }
  }

  // Calculate score (good patterns - issues)
  results.score -= results.issues.filter((i) => i.severity === "error").length * 2;
  results.score -= results.issues.filter((i) => i.severity === "warning").length * 1;

  return results;
}

function generateReport(results) {
  console.log("ðŸ§ª Test Architecture Validation Report\n");

  const totalFiles = results.length;
  const filesWithIssues = results.filter((r) => r.issues.length > 0).length;
  const filesWithGoodPatterns = results.filter((r) => r.goodPatterns.length > 0).length;

  console.log(`ðŸ“Š Summary:`);
  console.log(`  Total test files: ${totalFiles}`);
  console.log(`  Files with issues: ${filesWithIssues}`);
  console.log(`  Files with good patterns: ${filesWithGoodPatterns}`);
  console.log(
    `  Architecture compliance: ${Math.round((1 - filesWithIssues / totalFiles) * 100)}%\n`
  );

  // Show files with issues
  const filesWithProblems = results.filter((r) => r.issues.length > 0);
  if (filesWithProblems.length > 0) {
    console.log("âš ï¸  Files with architecture issues:\n");

    filesWithProblems.forEach((result) => {
      console.log(`ðŸ“„ ${result.file.replace(process.cwd(), ".")}`);
      result.issues.forEach((issue) => {
        const icon = issue.severity === "error" ? "âŒ" : issue.severity === "warning" ? "âš ï¸" : "â„¹ï¸";
        console.log(`  ${icon} ${issue.message}`);
      });
      console.log();
    });
  }

  // Show files with good patterns
  const filesWithGoodStuff = results.filter((r) => r.goodPatterns.length > 0);
  if (filesWithGoodStuff.length > 0) {
    console.log("âœ… Files following good patterns:\n");

    filesWithGoodStuff.slice(0, 5).forEach((result) => {
      console.log(`ðŸ“„ ${result.file.replace(process.cwd(), ".")} (score: ${result.score})`);
      result.goodPatterns.forEach((pattern) => {
        console.log(`  âœ… Uses ${pattern}`);
      });
      console.log();
    });
  }

  console.log("ðŸ“– See tests/TESTING_ARCHITECTURE.md for guidelines");
}

// Main execution
const testFiles = findTestFiles(join(process.cwd(), "tests"));
const results = testFiles.map(analyzeTestFile);

generateReport(results);

// Exit with error code if critical issues found
const criticalIssues = results.some((r) => r.issues.some((i) => i.severity === "error"));

process.exit(criticalIssues ? 1 : 0);

