// @vitest-environment node
import { describe, it, expect, vi } from "vitest";
import { setupMockDataset } from "../helpers/vectorSearch/mockDataset.js";

vi.mock("../../src/helpers/dataUtils.js", () => ({
  fetchJson: vi.fn(),
  validateWithSchema: vi.fn().mockResolvedValue(undefined)
}));

describe("queryRag", () => {
  it("expands synonyms and returns expected matches", async () => {
    // Ensure loader uses JSON manifest path for this test
    process.env.RAG_FORCE_JSON = "1";
    const dataset = [
      {
        id: "grip",
        text: "kumi kata basics grip fighting",
        embedding: [1, 0],
        source: "doc1",
        tags: []
      },
      {
        id: "other",
        text: "other topic",
        embedding: [0, 1],
        source: "doc2",
        tags: []
      }
    ];
    await setupMockDataset(dataset);

    const extractor = vi.fn(async () => [1, 0]);
    const getExtractorMock = vi.fn(async () => extractor);
    vi.doMock("../../src/helpers/api/vectorSearchPage.js", () => ({
      getExtractor: getExtractorMock
    }));

    vi.doMock("../../src/helpers/vectorSearch/index.js", async () => {
      const actual = await vi.importActual("../../src/helpers/vectorSearch/index.js");
      return {
        default: {
          ...actual.default,
          expandQueryWithSynonyms: vi.fn(async (q) => `${q} kumi kata`),
          // Return our dataset directly so queryRag hits expected entries
          findMatches: vi.fn(async (_vec, _k, _filters, _q) => dataset)
        }
      };
    });

    const { queryRag } = await import("../../src/helpers/queryRag.js");
    const results = await queryRag("grip fighting");
    delete process.env.RAG_FORCE_JSON;

    const vectorSearch = await import("../../src/helpers/vectorSearch/index.js");
    expect(vectorSearch.default.expandQueryWithSynonyms).toHaveBeenCalledWith("grip fighting");
    expect(extractor).toHaveBeenCalledWith("grip fighting kumi kata", { pooling: "mean" });
    expect(results.length).toBeGreaterThanOrEqual(1);
    expect(results[0].id).toBe("grip");
  });
});

// @vitest-environment node
import { describe, it, expect, vi } from "vitest";

describe("checkRagPreflight", () => {
  it("passes when offline artifacts consistent and strict offline disabled", async () => {
    // Mock fs to simulate consistent artifacts
    vi.doMock("node:fs/promises", () => ({
      readFile: vi.fn(async (p, enc) => {
        const pathStr = String(p);
        if (pathStr.endsWith("offline_rag_metadata.json")) {
          return JSON.stringify({ vectorLength: 3, count: 2, items: [] });
        }
        // vectors.bin length must be vectorLength * count = 6 bytes
        return Buffer.from([1, 2, 3, 4, 5, 6]);
      }),
      stat: vi.fn(async () => ({ size: 1 }))
    }));

    const mod = await import("../../scripts/checkRagPreflight.mjs");
    const res = await mod.checkPreflight();
    expect(res.ok).toBe(true);
    expect(res.errors).toEqual([]);
  });

  it("reports missing model files when strict offline is enabled", async () => {
    vi.doMock("node:fs/promises", () => ({
      readFile: vi.fn(async (p, enc) => {
        if (String(p).endsWith("offline_rag_metadata.json")) {
          return JSON.stringify({ vectorLength: 1, count: 1, items: [] });
        }
        return Buffer.from([7]);
      }),
      stat: vi.fn(async (p) => {
        // Simulate ENOENT for model files by throwing on paths containing /models/minilm/
        const s = String(p);
        if (s.includes("models") && s.includes("minilm")) {
          throw Object.assign(new Error("ENOENT"), { code: "ENOENT" });
        }
        return { size: 1 };
      })
    }));

    const mod = await import("../../scripts/checkRagPreflight.mjs");
    const res = await mod.checkStrictOfflineModel({ RAG_STRICT_OFFLINE: "1" });
    expect(res.ok).toBe(false);
    expect(res.errors.length).toBeGreaterThan(0);
  });
});

// @vitest-environment node
import { describe, it, expect, vi } from "vitest";
import { mkdtemp, writeFile, mkdir, rm, stat, readFile } from "node:fs/promises";
import path from "node:path";
import os from "node:os";

async function createSeedModelDir() {
  const tmp = await mkdtemp(path.join(os.tmpdir(), "minilm-seed-"));
  const onnxDir = path.join(tmp, "onnx");
  await mkdir(onnxDir, { recursive: true });
  await writeFile(path.join(tmp, "config.json"), "{}", "utf8");
  await writeFile(path.join(tmp, "tokenizer.json"), "{}", "utf8");
  await writeFile(path.join(tmp, "tokenizer_config.json"), "{}", "utf8");
  await writeFile(path.join(onnxDir, "model_quantized.onnx"), Buffer.from([1, 2, 3]));
  return tmp;
}

describe("prepareLocalModel", () => {
  it("copies required files from --from-dir into src/models/minilm", async () => {
    const seed = await createSeedModelDir();
    const { prepareLocalModel } = await import("../../scripts/prepareLocalModel.mjs");

    // Clean any existing dest
    await rm(path.join(process.cwd(), "src", "models", "minilm"), { recursive: true, force: true });

    const res = await prepareLocalModel({ fromDir: seed });
    expect(res.ok).toBe(true);
    expect(res.source).toBe("from-dir");

    const expected = [
      "config.json",
      "tokenizer.json",
      "tokenizer_config.json",
      path.join("onnx", "model_quantized.onnx")
    ];
    for (const rel of expected) {
      const p = path.join(process.cwd(), "src", "models", "minilm", rel);
      const s = await stat(p);
      expect(s.size).toBeGreaterThan(0);
    }
  });

  it("allows getExtractor to use local model path when present", async () => {
    // Arrange: ensure minimal config exists to take local path branch
    const dest = path.join(process.cwd(), "src", "models", "minilm");
    await mkdir(path.join(dest, "onnx"), { recursive: true });
    await writeFile(path.join(dest, "config.json"), "{}", "utf8");

    // Mock transformers pipeline; ensure called with local modelDir not remote name
    const pipelineMock = vi.fn(async () => ({
      // Dummy extractor that returns small vector-like result
      apply: async (t) => [0, 0]
    }));
    vi.doMock("@xenova/transformers", () => ({
      pipeline: pipelineMock,
      env: { allowLocalModels: false, localModelPath: "", backends: { onnx: { wasm: {} } } }
    }));

    // Ensure stat check passes regardless of environment peculiarities
    vi.doMock("fs/promises", () => ({
      stat: vi.fn(async () => ({ size: 1 }))
    }));

    // Act
    const { getExtractor } = await import("../../src/helpers/api/vectorSearchPage.js");
    await getExtractor();

    // Assert
    expect(pipelineMock).toHaveBeenCalled();
    const args = pipelineMock.mock.calls[0];
    expect(args[0]).toBe("feature-extraction");
    // Second arg should be a path ending with models/minilm
    expect(String(args[1])).toMatch(/models[\\\/]minilm$/);
  });
});
