#!/usr/bin/env node

/**
 * Event System Audit - Analyze battle event naming patterns
 *
 * @pseudocode
 * 1. Parse event emission files to extract event names and patterns
 * 2. Parse event listener files to find consumption patterns
 * 3. Parse test files to identify test-specific event usage
 * 4. Categorize events by type and usage
 * 5. Generate recommendations for naming standardization
 */

import fs from "fs";
import path from "path";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const projectRoot = path.resolve(__dirname, "..");

/**
 * Extract event names from emission patterns
 */
function parseEventEmissions(content) {
  const events = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match emitBattleEvent patterns
    const emitMatch = line.match(/emitBattleEvent\s*\(\s*["']([^"']+)["']/);
    if (emitMatch) {
      events.push({
        name: emitMatch[1],
        type: "battleEvent",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match eventBus.emit patterns
    const eventBusMatch = line.match(/eventBus\.emit\s*\(\s*["']([^"']+)["']/);
    if (eventBusMatch) {
      events.push({
        name: eventBusMatch[1],
        type: "eventBus",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match generic emit patterns
    const genericEmitMatch = line.match(/\.emit\s*\(\s*["']([^"']+)["']/);
    if (genericEmitMatch) {
      events.push({
        name: genericEmitMatch[1],
        type: "generic",
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return events;
}

/**
 * Extract event listener patterns
 */
function parseEventListeners(content) {
  const listeners = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match addEventListener patterns
    const addEventMatch = line.match(/addEventListener\s*\(\s*["']([^"']+)["']/);
    if (addEventMatch) {
      listeners.push({
        event: addEventMatch[1],
        type: "addEventListener",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match .on( patterns
    const onMatch = line.match(/\.on\s*\(\s*["']([^"']+)["']/);
    if (onMatch) {
      listeners.push({
        event: onMatch[1],
        type: "on",
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match setupPromise patterns
    const promiseMatch = line.match(/setupPromise\s*\(\s*["']([^"']+)["']/);
    if (promiseMatch) {
      listeners.push({
        event: promiseMatch[1],
        type: "promise",
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return listeners;
}

/**
 * Extract test event usage patterns
 */
function parseTestEventUsage(content) {
  const testEvents = [];
  const lines = content.split("\n");

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();

    // Match get*Promise patterns
    const promiseMatch = line.match(/get(\w+)Promise/);
    if (promiseMatch) {
      testEvents.push({
        promiseName: `get${promiseMatch[1]}Promise`,
        eventType: promiseMatch[1],
        file: extractFileFromPath(line),
        line: line.trim()
      });
      continue;
    }

    // Match waitFor*Event patterns
    const waitMatch = line.match(/waitFor(\w+)Event/);
    if (waitMatch) {
      testEvents.push({
        waitName: `waitFor${waitMatch[1]}Event`,
        eventType: waitMatch[1],
        file: extractFileFromPath(line),
        line: line.trim()
      });
    }
  }

  return testEvents;
}

/**
 * Extract file path from grep output line
 */
function extractFileFromPath(line) {
  const match = line.match(/^([^:]+):/);
  return match ? path.basename(match[1]) : "unknown";
}

/**
 * Categorize events by naming patterns
 */
function categorizeEvents(events) {
  const categories = {
    timer: [],
    ui: [],
    state: [],
    player: [],
    scoreboard: [],
    debug: [],
    control: [],
    uncategorized: []
  };

  for (const event of events) {
    const name = event.name.toLowerCase();

    if (name.includes("timer") || name.includes("timeout") || name.includes("countdown")) {
      categories.timer.push(event);
    } else if (name.includes("button") || name.includes("card") || name.includes("ui")) {
      categories.ui.push(event);
    } else if (name.includes("state") || name.includes("match") || name.includes("round")) {
      categories.state.push(event);
    } else if (name.includes("player") || name.includes("stat") || name.includes("select")) {
      categories.player.push(event);
    } else if (name.includes("scoreboard") || name.includes("message")) {
      categories.scoreboard.push(event);
    } else if (name.includes("debug") || name.includes("panel")) {
      categories.debug.push(event);
    } else if (name.includes("control") || name.includes("event")) {
      categories.control.push(event);
    } else {
      categories.uncategorized.push(event);
    }
  }

  return categories;
}

/**
 * Generate naming convention recommendations
 */
function generateNamingConventions(categories) {
  return {
    proposed: {
      "timer.*": [
        "timer.roundExpired",
        "timer.countdownStarted",
        "timer.statSelectionExpired",
        "timer.cooldownFinished"
      ],
      "ui.*": [
        "ui.statButtonsEnabled",
        "ui.statButtonsDisabled",
        "ui.cardsRevealed",
        "ui.countdownStarted"
      ],
      "state.*": [
        "state.transitioned",
        "state.matchStarted",
        "state.roundStarted",
        "state.matchOver"
      ],
      "player.*": ["player.statSelected", "player.interrupted", "player.actionTimeout"],
      "scoreboard.*": [
        "scoreboard.messageShown",
        "scoreboard.messageCleared",
        "scoreboard.scoreUpdated"
      ],
      "debug.*": ["debug.panelUpdated", "debug.stateExposed"]
    },
    migration: {
      // Old name -> New name mappings
      roundTimeout: "timer.roundExpired",
      "statButtons:enable": "ui.statButtonsEnabled",
      "statButtons:disable": "ui.statButtonsDisabled",
      scoreboardShowMessage: "scoreboard.messageShown",
      scoreboardClearMessage: "scoreboard.messageCleared",
      debugPanelUpdate: "debug.panelUpdated",
      matchOver: "state.matchOver",
      statSelected: "player.statSelected"
    }
  };
}

/**
 * Main analysis function
 */
async function analyzeEventSystem() {
  console.log("# Event System Audit Report");
  console.log(`Generated: ${new Date().toISOString()}`);
  console.log("");

  try {
    // Read audit files
    const emissionsFile = path.join(projectRoot, "design/eventAudit/eventEmissions.txt");
    const listenersFile = path.join(projectRoot, "design/eventAudit/eventListeners.txt");
    const testUsageFile = path.join(projectRoot, "design/eventAudit/testEventUsage.txt");

    const emissionsContent = fs.readFileSync(emissionsFile, "utf8");
    const listenersContent = fs.readFileSync(listenersFile, "utf8");
    const testUsageContent = fs.readFileSync(testUsageFile, "utf8");

    // Parse content
    const events = parseEventEmissions(emissionsContent);
    const listeners = parseEventListeners(listenersContent);
    const testEvents = parseTestEventUsage(testUsageContent);

    console.log("## Summary");
    console.log("");
    console.log(`- **Events emitted**: ${events.length}`);
    console.log(`- **Event listeners**: ${listeners.length}`);
    console.log(`- **Test event patterns**: ${testEvents.length}`);
    console.log("");

    // Categorize events
    const categories = categorizeEvents(events);

    console.log("## Event Categories");
    console.log("");
    for (const [category, categoryEvents] of Object.entries(categories)) {
      if (categoryEvents.length > 0) {
        console.log(
          `### ${category.charAt(0).toUpperCase() + category.slice(1)} Events (${categoryEvents.length})`
        );
        console.log("");
        const uniqueNames = [...new Set(categoryEvents.map((e) => e.name))];
        for (const name of uniqueNames.sort()) {
          console.log(`- \`${name}\``);
        }
        console.log("");
      }
    }

    console.log("## Current Event Names Analysis");
    console.log("");
    const uniqueEvents = [...new Set(events.map((e) => e.name))].sort();
    console.log("**All unique event names currently in use:**");
    console.log("```");
    uniqueEvents.forEach((name) => console.log(`"${name}"`));
    console.log("```");
    console.log("");

    // Generate naming conventions
    const conventions = generateNamingConventions(categories);

    console.log("## Recommended Naming Convention");
    console.log("");
    console.log("### Proposed Structure");
    console.log("");
    for (const [namespace, examples] of Object.entries(conventions.proposed)) {
      console.log(`**${namespace}**: ${examples.join(", ")}`);
    }
    console.log("");

    console.log("## Migration Mapping");
    console.log("");
    console.log("### High-Priority Migrations");
    console.log("");
    console.log("| Current Name | Proposed Name | Category | Priority |");
    console.log("|--------------|---------------|----------|----------|");

    for (const [oldName, newName] of Object.entries(conventions.migration)) {
      const category =
        Object.keys(categories).find((cat) => categories[cat].some((e) => e.name === oldName)) ||
        "uncategorized";
      const priority = category === "timer" || category === "state" ? "High" : "Medium";
      console.log(`| \`${oldName}\` | \`${newName}\` | ${category} | ${priority} |`);
    }

    console.log("");
    console.log("## Test Integration Points");
    console.log("");
    if (testEvents.length > 0) {
      console.log("**Test helper functions that need updating:**");
      const uniqueTestEvents = [
        ...new Set(testEvents.map((e) => e.promiseName || e.waitName))
      ].sort();
      uniqueTestEvents.forEach((name) => {
        if (name) console.log(`- \`${name}\``);
      });
    } else {
      console.log("No test-specific event patterns detected.");
    }
    console.log("");

    console.log("## Implementation Recommendations");
    console.log("");
    console.log("1. **Implement backward-compatible alias system** to avoid breaking changes");
    console.log("2. **Update emitters gradually** using feature flags");
    console.log("3. **Create migration timeline** with deprecation warnings");
    console.log("4. **Update test helpers** to use new event names with backward compatibility");
    console.log("5. **Document event contracts** for future consistency");
  } catch (error) {
    console.error("‚ùå Analysis failed:", error.message);
    process.exit(1);
  }
}

// Run the analysis
analyzeEventSystem().catch(console.error);

#!/usr/bin/env node

/**
 * State Handler Contract Compliance Audit
 *
 * Analyzes state handlers against their documented contracts in stateTable.js
 *
 * @pseudocode
 * 1. Parse stateTable.js to extract state definitions and onEnter contracts
 * 2. Scan stateHandlers directory for implemented handlers
 * 3. Map contracts to actual implementations
 * 4. Generate compliance matrix with gap analysis
 * 5. Categorize gaps by priority (blocking, important, nice-to-have)
 */

import fs from "fs";
import path from "path";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Project root directory
const projectRoot = path.resolve(__dirname, "..");

/**
 * Parse state table to extract contracts
 */
async function parseStateContracts() {
  const stateTablePath = path.join(projectRoot, "src/helpers/classicBattle/stateTable.js");
  const stateTableContent = fs.readFileSync(stateTablePath, "utf8");

  // Extract the CLASSIC_BATTLE_STATES array
  const stateDefRegex = /export const CLASSIC_BATTLE_STATES = (\[[\s\S]*?\]);/;
  const match = stateTableContent.match(stateDefRegex);

  if (!match) {
    throw new Error("Could not parse CLASSIC_BATTLE_STATES from stateTable.js");
  }

  try {
    // Use eval to parse the array (safe since we control the source)
    const statesArray = eval(match[1]);
    const contracts = {};

    for (const state of statesArray) {
      contracts[state.name] = {
        id: state.id,
        description: state.description,
        onEnter: state.onEnter || [],
        type: state.type || "normal"
      };
    }

    return contracts;
  } catch (error) {
    console.error("Error parsing state definitions:", error);
    throw error;
  }
}

/**
 * Scan state handler directory for implementations
 */
async function scanStateHandlers() {
  const handlersDir = path.join(projectRoot, "src/helpers/classicBattle/stateHandlers");
  const handlers = {};

  try {
    const files = fs.readdirSync(handlersDir);

    for (const file of files) {
      if (file.endsWith(".js")) {
        const filePath = path.join(handlersDir, file);
        const content = fs.readFileSync(filePath, "utf8");

        // Extract state name from filename (e.g., waitingForPlayerActionEnter.js -> waitingForPlayerAction)
        const match = file.match(/^(.+?)(Enter|Exit)\.js$/);
        if (match) {
          const stateName = match[1];
          const handlerType = match[2].toLowerCase(); // 'enter' or 'exit'

          if (!handlers[stateName]) {
            handlers[stateName] = {};
          }

          handlers[stateName][handlerType] = {
            file: file,
            path: filePath,
            exists: true,
            content: content
          };
        }
      }
    }
  } catch (error) {
    console.error("Error scanning state handlers:", error);
    throw error;
  }

  return handlers;
}

/**
 * Analyze handler implementation for contract compliance
 */
function analyzeHandlerImplementation(handler, contractActions) {
  if (!handler || !handler.content) {
    return {
      implemented: [],
      missing: contractActions,
      analysis: "Handler file not found"
    };
  }

  const content = handler.content;
  const implemented = [];
  const missing = [];

  for (const action of contractActions) {
    const [category, operation] = action.split(":");
    let isImplemented = false;

    // Check for different implementation patterns
    switch (category) {
      case "timer":
        isImplemented =
          content.includes("startTimer") ||
          content.includes("timerService") ||
          content.includes("timer") ||
          content.includes("clearTimer");
        break;

      case "render":
      case "announce":
      case "prompt":
        isImplemented =
          content.includes("emitBattleEvent") ||
          content.includes("battleEvent") ||
          content.includes("render") ||
          content.includes("announce") ||
          content.includes("prompt");
        break;

      case "init":
      case "store":
      case "reset":
      case "set":
        isImplemented =
          content.includes("store") ||
          content.includes("context") ||
          content.includes("machine") ||
          content.includes("reset") ||
          content.includes("init");
        break;

      case "draw":
      case "reveal":
        isImplemented =
          content.includes("draw") ||
          content.includes("reveal") ||
          content.includes("cards") ||
          content.includes("judoka");
        break;

      case "compare":
      case "compute":
        isImplemented =
          content.includes("compare") ||
          content.includes("compute") ||
          content.includes("outcome") ||
          content.includes("result");
        break;

      case "update":
        isImplemented =
          content.includes("update") || content.includes("score") || content.includes("UI");
        break;

      case "show":
        isImplemented =
          content.includes("show") || content.includes("display") || content.includes("screen");
        break;

      case "open":
        isImplemented =
          content.includes("open") || content.includes("panel") || content.includes("modal");
        break;

      case "rollback":
      case "teardown":
        isImplemented =
          content.includes("rollback") ||
          content.includes("teardown") ||
          content.includes("cleanup") ||
          content.includes("reset");
        break;

      case "log":
        isImplemented =
          content.includes("log") || content.includes("analytics") || content.includes("console");
        break;

      case "a11y":
        isImplemented =
          content.includes("a11y") ||
          content.includes("accessibility") ||
          content.includes("aria") ||
          content.includes("timer"); // Timer status is handled by timerService
        break;

      default:
        // Generic check for the action term
        isImplemented = content.toLowerCase().includes(operation.toLowerCase());
    }

    if (isImplemented) {
      implemented.push(action);
    } else {
      missing.push(action);
    }
  }

  return {
    implemented,
    missing,
    analysis:
      missing.length === 0
        ? "Fully compliant"
        : missing.length === contractActions.length
          ? "No implementation found"
          : "Partially implemented"
  };
}

/**
 * Categorize gaps by priority
 */
function categorizeGaps(missing, stateName) {
  const priority1 = []; // Blocking: Timer logic, state transitions
  const priority2 = []; // Important: UI updates, announcements
  const priority3 = []; // Nice-to-have: Logging, optional features

  for (const action of missing) {
    const [category, operation] = action.split(":");

    if (
      category === "timer" ||
      (category === "init" && stateName === "matchStart") ||
      (category === "compare" && stateName === "roundDecision")
    ) {
      priority1.push(action);
    } else if (
      category === "render" ||
      category === "announce" ||
      category === "prompt" ||
      category === "update" ||
      category === "show"
    ) {
      priority2.push(action);
    } else {
      priority3.push(action);
    }
  }

  return { priority1, priority2, priority3 };
}

/**
 * Generate compliance report
 */
async function generateReport() {
  console.log("# State Handler Contract Compliance Audit");
  console.log(`Generated: ${new Date().toISOString()}`);
  console.log("");

  try {
    const contracts = await parseStateContracts();
    const handlers = await scanStateHandlers();

    console.log("## Summary");
    console.log("");
    console.log(`- **States in contract**: ${Object.keys(contracts).length}`);
    console.log(`- **Handler files found**: ${Object.keys(handlers).length}`);
    console.log("");

    // Overall compliance stats
    let totalActions = 0;
    let totalImplemented = 0;
    let totalMissing = 0;
    let criticalGaps = 0;

    console.log("## Detailed Analysis");
    console.log("");

    for (const [stateName, contract] of Object.entries(contracts)) {
      console.log(`### ${stateName}`);
      console.log(`**ID**: ${contract.id} | **Type**: ${contract.type}`);
      console.log(`**Description**: ${contract.description}`);
      console.log("");

      if (contract.onEnter.length === 0) {
        console.log("‚úÖ **No onEnter actions required**");
        console.log("");
        continue;
      }

      console.log(`**Required onEnter actions**: ${contract.onEnter.length}`);
      console.log("```");
      contract.onEnter.forEach((action) => console.log(`- ${action}`));
      console.log("```");
      console.log("");

      // Check handler implementation
      const handler = handlers[stateName]?.enter;
      const analysis = analyzeHandlerImplementation(handler, contract.onEnter);

      totalActions += contract.onEnter.length;
      totalImplemented += analysis.implemented.length;
      totalMissing += analysis.missing.length;

      console.log(`**Implementation Status**: ${analysis.analysis}`);

      if (analysis.implemented.length > 0) {
        console.log(
          `**‚úÖ Implemented (${analysis.implemented.length})**: ${analysis.implemented.join(", ")}`
        );
      }

      if (analysis.missing.length > 0) {
        console.log(`**‚ùå Missing (${analysis.missing.length})**: ${analysis.missing.join(", ")}`);

        // Categorize gaps
        const gaps = categorizeGaps(analysis.missing, stateName);
        if (gaps.priority1.length > 0) {
          console.log(`**üö® Priority 1 (Critical)**: ${gaps.priority1.join(", ")}`);
          criticalGaps += gaps.priority1.length;
        }
        if (gaps.priority2.length > 0) {
          console.log(`**‚ö†Ô∏è Priority 2 (Important)**: ${gaps.priority2.join(", ")}`);
        }
        if (gaps.priority3.length > 0) {
          console.log(`**‚ÑπÔ∏è Priority 3 (Nice-to-have)**: ${gaps.priority3.join(", ")}`);
        }
      }

      if (handler) {
        console.log(`**Handler file**: \`${handler.file}\``);
      } else {
        console.log("**‚ùå Handler file**: Not found");
      }

      console.log("");
      console.log("---");
      console.log("");
    }

    // Final summary
    console.log("## Compliance Summary");
    console.log("");
    console.log(`- **Total contract actions**: ${totalActions}`);
    console.log(
      `- **Implemented**: ${totalImplemented} (${Math.round((totalImplemented / totalActions) * 100)}%)`
    );
    console.log(
      `- **Missing**: ${totalMissing} (${Math.round((totalMissing / totalActions) * 100)}%)`
    );
    console.log(`- **Critical gaps**: ${criticalGaps}`);
    console.log("");

    if (criticalGaps > 0) {
      console.log(
        "üö® **Critical gaps found!** These should be addressed first as they may break battle flow."
      );
    } else if (totalMissing > 0) {
      console.log("‚ö†Ô∏è **Non-critical gaps found.** Consider implementing for completeness.");
    } else {
      console.log("‚úÖ **All state handlers are fully compliant with their contracts.**");
    }

    console.log("");
    console.log("## Recommendations");
    console.log("");
    console.log("1. **Address Priority 1 gaps first** - these can break core battle functionality");
    console.log(
      "2. **Verify implementation patterns** - some actions may be implemented but not detected by this audit"
    );
    console.log("3. **Add unit tests** for any modified handlers");
    console.log("4. **Run integration tests** after fixes to ensure no regressions");
  } catch (error) {
    console.error("‚ùå Audit failed:", error.message);
    process.exit(1);
  }
}

// Run the audit
generateReport().catch(console.error);

#!/usr/bin/env node

/**
 * Validate test architecture patterns across the codebase
 * Checks for anti-patterns and suggests improvements
 */

import { readFileSync, readdirSync, statSync } from "fs";
import { join, extname } from "path";

const ANTI_PATTERNS = {
  manualDomForComplex: {
    pattern: /document\.body\.innerHTML.*=.*['"`].*(<.*>.*){3,}/,
    message: "Complex manual DOM detected - consider using real HTML for integration tests",
    severity: "warning"
  },
  missingCleanup: {
    pattern: /createRealHtmlTestEnvironment\(\)/,
    requiresPattern: /cleanup\(\)/,
    message: "Real HTML test environment created but cleanup() not found",
    severity: "error"
  },
  inconsistentNaming: {
    pattern: /describe\(['"`][^'"`]*['"`]/,
    message: "Consider using consistent naming: Component (Unit|DOM|Integration)",
    severity: "info"
  }
};

const GOOD_PATTERNS = {
  realHtmlUtils: /createRealHtmlTestEnvironment|validateRealHtmlStructure/,
  properCleanup: /afterEach.*cleanup/,
  clearNaming: /describe\(['"`][^'"`]*\s*\((Unit|DOM|Integration)\)/
};

function findTestFiles(dir, files = []) {
  const items = readdirSync(dir);

  for (const item of items) {
    const fullPath = join(dir, item);
    const stat = statSync(fullPath);

    if (stat.isDirectory() && !item.includes("node_modules")) {
      findTestFiles(fullPath, files);
    } else if (item.endsWith(".test.js") || item.endsWith(".spec.js")) {
      files.push(fullPath);
    }
  }

  return files;
}

function analyzeTestFile(filePath) {
  const content = readFileSync(filePath, "utf-8");
  const results = {
    file: filePath,
    issues: [],
    goodPatterns: [],
    score: 0
  };

  // Check for anti-patterns
  for (const [name, config] of Object.entries(ANTI_PATTERNS)) {
    if (config.pattern.test(content)) {
      if (config.requiresPattern && !config.requiresPattern.test(content)) {
        results.issues.push({
          type: name,
          message: config.message,
          severity: config.severity
        });
      } else if (!config.requiresPattern) {
        results.issues.push({
          type: name,
          message: config.message,
          severity: config.severity
        });
      }
    }
  }

  // Check for good patterns
  for (const [name, pattern] of Object.entries(GOOD_PATTERNS)) {
    if (pattern.test(content)) {
      results.goodPatterns.push(name);
      results.score += 1;
    }
  }

  // Calculate score (good patterns - issues)
  results.score -= results.issues.filter((i) => i.severity === "error").length * 2;
  results.score -= results.issues.filter((i) => i.severity === "warning").length * 1;

  return results;
}

function generateReport(results) {
  console.log("üß™ Test Architecture Validation Report\n");

  const totalFiles = results.length;
