{"id":"uiHelpers.js-applyBattleFeatureFlags","text":"Apply battle-related feature flags to the page.

@param {HTMLElement|null} battleArea
@param {HTMLElement|null} banner
1. Set mode/test-mode data attributes on `battleArea`.
2. Toggle test banner visibility and various debug features.
3. Reapply flags when `featureFlagsEmitter` emits changes.

export function applyBattleFeatureFlags(battleArea, banner) {
  if (battleArea) {
    battleArea.dataset.mode = "classic";
    battleArea.dataset.testMode = String(isEnabled("enableTestMode"));
  }
  if (banner) banner.classList.toggle("hidden", !isEnabled("enableTestMode"));
  setTestMode(isEnabled("enableTestMode"));
  toggleInspectorPanels(isEnabled("enableCardInspector"));
  toggleViewportSimulation(isEnabled("viewportSimulation"));
  setDebugPanelEnabled(isEnabled("battleDebugPanel") || isEnabled("enableTestMode"));

  featureFlagsEmitter.addEventListener("change", () => {
    if (battleArea) battleArea.dataset.testMode = String(isEnabled("enableTestMode"));
    if (banner) banner.classList.toggle("hidden", !isEnabled("enableTestMode"));
    setTestMode(isEnabled("enableTestMode"));
    toggleInspectorPanels(isEnabled("enableCardInspector"));
    toggleViewportSimulation(isEnabled("viewportSimulation"));
    setDebugPanelEnabled(isEnabled("battleDebugPanel") || isEnabled("enableTestMode"));
  });
}","qaContext":"Why: Apply battle-related feature flags to the page. How: @param {HTMLElement|null} battleArea @param {HTMLElement|null} banner 1.","embedding":[-0.036,0.082,-0.056,0.077,0.041,0.178,0.11,0.002,-0.164,-0.004,0.09,-0.153,-0.026,-0.039,0.062,0.108,0.051,-0.097,-0.003,0.049,0.08,-0.089,0.097,-0.009,0.076,0.061,-0.009,-0.051,-0.1,-0.058,0.06,0.025,-0.201,-0.007,0.073,-0.107,-0.06,-0.017,-0.062,-0.042,-0.11,-0.003,-0.023,-0.11,-0.014,-0.062,-0.037,0.057,0.037,-0.003,0.1,-0.044,0.033,-0.065,0.096,0.011,0.025,0.115,0.046,0.113,-0.006,-0.003,-0.014,0.003,-0.05,-0.025,0.04,-0.051,-0.067,0.032,-0.053,0.127,0.181,-0.089,0.048,-0.04,-0.129,0.053,0.036,-0.256,-0.004,-0.059,-0.119,0.059,0.062,0.013,0.07,-0.063,-0.004,0.07,-0.091,0.111,0.028,0.102,-0.039,0.078,-0.034,0.02,-0.076,0.18,0.011,-0.069,-0.103,0.109,0.01,-0.15,0.102,-0.046,-0.067,0.101,0.074,0.111,0.005,0.033,-0.116,0.03,0.135,0.002,0.027,-0.028,0.198,-0.014,0,-0.163,0.152,0.013,0.001,0,0.194,-0.173,-0.204,0.165,-0.043,0.114,0.07,0.057,-0.05,0.026,0.018,0.028,-0.041,0.01,0.101,-0.081,-0.024,0.016,-0.003,-0.053,0.006,-0.091,-0.08,-0.093,-0.054,0.103,-0.061,0.073,-0.2,0.054,0.072,-0.031,0.013,0.147,-0.009,-0.098,-0.067,-0.137,-0.003,0.057,-0.055,-0.131,-0.067,-0.113,-0.127,-0.081,-0.041,-0.022,0.014,-0.053,-0.048,0.147,0.019,-0.272,-0.024,0.025,-0.178,0.046,-0.046,0.081,-0.136,0.056,0.043,-0.067,-0.111,0.166,-0.029,-0.181,-0.086,-0.338,-0.033,-0.133,0.232,-0.104,0.008,0.05,-0.042,0.128,-0.108,-0.138,0.095,-0.1,-0.064,0.077,0.076,-0.075,0.027,-0.162,-0.127,0.075,-0.051,0.116,0.066,-0.199,0.116,0,0.014,0.122,-0.026,-0.053,0.065,0.004,0.055,0.071,-0.178,-0.048,0.036,-0.065,-0.199,-0.068,-0.124,0.226,0.024,0.054,0.058,0.089,0.011,0.235,0.062,0.03,-0.054,-0.064,0.058,0.11,0.128,-0.06,0.136,0.06,-0.22,-0.033,0.075,-0.109,0.013,-0.025,-0.145,0.188,0.101,-0.043,-0.041,0.068,0.032,0.067,0.025,0.053,-0.033,0.008,0.018,-0.176,0.03,-0.017,-0.013,-0.026,0.083,-0.167,-0.119,0.118,0.143,-0.084,0.054,0.066,0.198,0.061,-0.0