    const clamped = Math.max(0, Math.min(index, pageCount - 1));
    this.currentPage = clamped;
    const left = clamped * pageWidth;
    // Instant scroll prevents animation queues from causing drift.
    // Suppress scroll event sync while performing programmatic scroll to
    // avoid intermediary scroll calculations from overriding the target page.
    this._suppressScrollSync = true;
    this.container.scrollTo({ left, behavior: "auto" });
    // Listen for the scroll to fully end before re-enabling scroll sync.
    const release = () => {
      this._suppressScrollSync = false;
      this.container.removeEventListener("scrollend", release);
    };
    this.container.addEventListener("scrollend", release);
    this.update();
  }

  /**
   * Updates the visual state of the carousel's navigation buttons and page indicators.
   * This method should be called after any change to the carousel's current page or metrics.
   *
   * @pseudocode
   * 1. Destructure `pageCount` from `this.metrics`.
   * 2. Update the disabled state of the `leftBtn`:
   *    a. If `this.leftBtn` exists, set its `disabled` property to `true` if `this.currentPage` is less than or equal to 0 (i.e., on the first page), otherwise `false`.
   * 3. Update the disabled state of the `rightBtn`:
   *    a. If `this.rightBtn` exists, set its `disabled` property to `true` if `this.currentPage` is greater than or equal to `pageCount - 1` (i.e., on the last page), otherwise `false`.
   * 4. Call `this._syncMarkers()` to update the visual appearance of the page markers and the page counter.
   *
   * @returns {void}
   */
  update() {
    // Update button disabled states
    const { pageCount } = this.metrics;
    if (this.leftBtn) this.leftBtn.disabled = this.currentPage <= 0;
    if (this.rightBtn) this.rightBtn.disabled = this.currentPage >= pageCount - 1;

    // Update markers and counter
    this._syncMarkers();
  }

  /**
   * Wires up keyboard navigation for the carousel container.
   * Allows users to navigate pages using ArrowLeft and ArrowRight keys.
   *
   * @private
   * @pseudocode
   * 1. Set the `scrollBehavior` style of `this.container` to "auto" for instant scrolling.
   * 2. Set the `tabIndex` of `this.container` to 0 to make it focusable.
   * 3. Define `this._onKeydown` as an event handler function for keydown events:
   *    a. If the event target is not `this.container`, exit the handler.
   *    b. If the pressed key (`event.key`) is "ArrowLeft":
   *       i. Prevent the default browser action for the keydown event.
   *       ii. If `event.stopImmediatePropagation` is a function, call it to prevent other listeners on the same element from being called.
   *       iii. Call `this.prev()` to navigate to the previous page.
   *    c. Else if the pressed key (`event.key`) is "ArrowRight":
   *       i. Prevent the default browser action for the keydown event.
   *       ii. If `event.stopImmediatePropagation` is a function, call it.
   *       iii. Call `this.next()` to navigate to the next page.
   * 4. Add `this._onKeydown` as a "keydown" event listener to `this.container`.
   *
   * @returns {void}
   */
  _wireKeyboard() {
    this.container.style.scrollBehavior = "auto";
    this.container.tabIndex = 0;
    this._onKeydown = (event) => {
      if (event.target !== this.container) return;
      if (event.key === "ArrowLeft") {
        event.preventDefault();
        if (typeof event.stopImmediatePropagation === "function") event.stopImmediatePropagation();
        this.prev();
      } else if (event.key === "ArrowRight") {
        event.preventDefault();
        if (typeof event.stopImmediatePropagation === "function") event.stopImmediatePropagation();
        this.next();
      }
    };
    this.container.addEventListener("keydown", this._onKeydown);
  }

  /**
   * Wires up swipe (touch and pointer) navigation for the carousel container.
   * Allows users to navigate pages by swiping left or right.
   *
   * @private
   * @pseudocode
   * 1. Initialize `startX` to 0 to store the starting X-coordinate of a touch/pointer event.
   * 2. Track `gestureActive` and `activeKind` to know if a swipe is in progress and its type.
   * 3. Define an `onEnd` helper function that takes `endX` (the ending X-coordinate):
   *    a. If `gestureActive` is false, exit.
   *    b. Reset `gestureActive`, `activeKind`, and `pointerDown`.
   *    c. Calculate `delta` as the difference between `endX` and `startX`.
   *    d. If `delta` is greater than `this.threshold`, call `this.prev()` to navigate to the previous page.
   *    e. Else if `delta` is less than negative `this.threshold`, call `this.next()` to navigate to the next page.
   * 4. Define a `reset` helper to clear `gestureActive`, `activeKind`, and `pointerDown` without navigating.
   * 5. Define `this._onTouchStart` as an event handler for "touchstart" events:
   *    a. If `gestureActive` is true, exit.
   *    b. Set `gestureActive` to true and `activeKind` to "touch".
   *    c. Store the `clientX` of the first touch in `startX`.
   * 6. Define `this._onTouchEnd` as an event handler for "touchend" events:
   *    a. Call `onEnd` with the `clientX` of the first changed touch.
   * 7. Define `this._onTouchCancel` as an event handler for "touchcancel" events:
   *    a. Call `reset` to abandon the gesture.
   * 8. Initialize `pointerDown` to `false` for tracking pointer (mouse) interactions.
   * 9. Define `this._onPointerDown` as an event handler for "pointerdown" events:
   *    a. Ignore events where `e.pointerType` is "touch" or `gestureActive` is true.
   *    b. Set `gestureActive` to true, `activeKind` to "pointer", and `pointerDown` to true.
   *    c. Store the `clientX` of the pointer event in `startX`.
   * 10. Define `this._onPointerUp` as an event handler for "pointerup" events:
   *    a. If `pointerDown` is `false`, exit the handler.
   *    b. Call `onEnd` with the `clientX` of the pointer event.
   * 11. Define `this._onPointerCancel` as an event handler for "pointercancel" events:
   *    a. Call `reset` to abandon the gesture.
   * 12. Add event listeners for "touchstart", "touchend", "touchmove", and "touchcancel".
   * 13. Add event listeners for "pointerdown", "pointerup", and "pointercancel".
   *
   * @returns {void}
   */
  _wireSwipe() {
    let startX = 0;
    let gestureActive = false;
    let activeKind = null;
    let pointerDown = false;

    const reset = () => {
      gestureActive = false;
      if (activeKind === "pointer") pointerDown = false;
      activeKind = null;
    };

    const onEnd = (endX) => {
      if (!gestureActive) return;
      const delta = endX - startX;
      reset();
      if (delta > this.threshold) {
        this.prev();
      } else if (delta < -this.threshold) {
        this.next();
      }
    };

    this._onTouchStart = (e) => {
      if (gestureActive) return;
      gestureActive = true;
      activeKind = "touch";
      startX = e.touches[0].clientX;
      if (typeof e.preventDefault === "function") e.preventDefault();
    };

    this._onTouchEnd = (e) => {
      onEnd(e.changedTouches[0].clientX);
      if (typeof e.preventDefault === "function") e.preventDefault();
    };

    this._onTouchMove = (e) => {
      if (typeof e.preventDefault === "function") e.preventDefault();
    };

    this._onTouchCancel = () => {
      reset();
    };

    this._onPointerDown = (e) => {
      if (e.pointerType === "touch" || gestureActive) return;
      gestureActive = true;
      activeKind = "pointer";
      pointerDown = true;
      startX = e.clientX;
    };

    this._onPointerUp = (e) => {
      if (!pointerDown) return;
      onEnd(e.clientX);
    };

    this._onPointerCancel = () => {
      reset();
    };

    this.container.addEventListener("touchstart", this._onTouchStart, { passive: false });
    this.container.addEventListener("touchend", this._onTouchEnd, { passive: false });
    this.container.addEventListener("touchmove", this._onTouchMove, { passive: false });
    this.container.addEventListener("touchcancel", this._onTouchCancel);
    this.container.addEventListener("pointerdown", this._onPointerDown);
    this.container.addEventListener("pointerup", this._onPointerUp);
    this.container.addEventListener("pointercancel", this._onPointerCancel);
  }
  "";

  /**
   * Wires up scroll event synchronization for the carousel container.
   * This method ensures that the `currentPage` and UI are updated when the user scrolls the carousel.
   * It uses both immediate and `requestAnimationFrame`-based synchronization for responsiveness.
   *
   * @private
   * @pseudocode
   * 1. Define `this._onScroll` as an event handler function for "scroll" events:
   *    a. If `this._suppressScrollSync` is true, exit the handler (programmatic scroll is in progress).
   *    b. If `this.metrics.pageWidth` is less than or equal to 0, recalculate `this.metrics` using `getPageMetrics`.
   *    c. Destructure `pageWidth` and `pageCount` from `this.metrics`.
   *    d. If `pageWidth` is greater than 0:
   *       i. Calculate `maxScroll` (maximum scrollable width).
   *       ii. Calculate `remaining` scroll distance.
   *       iii. Determine the `page` based on `scrollLeft` and `pageWidth`, handling the last page edge case.
   *       iv. Update `this.currentPage` by clamping the calculated `page` within valid bounds.
   *       v. Call `this.update()` to refresh the UI immediately.
   *    e. If a previous `_rafId` exists, cancel the associated animation frame.
   *    f. Request a new animation frame (`this._rafId`) to perform a follow-up synchronization:
   *       i. In the animation frame callback, if `this._suppressScrollSync` is true, exit.
   *       ii. If `this.metrics.pageWidth` is less than or equal to 0, recalculate `this.metrics`.
   *       iii. Destructure `pageWidth` (`pw`) and `pageCount` (`pc`) from `this.metrics`.
   *       iv. If `pw` is less than or equal to 0, exit.
   *       v. Recalculate `maxScroll`, `remaining`, and `page` based on current scroll position and metrics.
   *       vi. Update `this.currentPage` by clamping the calculated `page`.
   *       vii. Call `this.update()` to refresh the UI.
   * 2. Add `this._onScroll` as a "scroll" event listener to `this.container`.
   *
   * @returns {void}
   */
  _wireScrollSync() {
    this._onScroll = () => {
      if (this._suppressScrollSync) return;
      if (this.metrics.pageWidth <= 0) {
        this.metrics = getPageMetrics(this.container);
      }
      // Immediate sync for tests and snappy UI
      const { pageWidth, pageCount } = this.metrics;
      if (pageWidth > 0) {
        const page = Math.round(this.container.scrollLeft / pageWidth);
        // (no-op) immediate scroll sync
        this.currentPage = Math.max(0, Math.min(page, pageCount - 1));
        this.update();
      }
      // Follow-up rAF sync to catch any late layout
      if (this._rafId) cancelAnimationFrame(this._rafId);
      this._rafId = requestAnimationFrame(() => {
        if (this._suppressScrollSync) return;
        if (this.metrics.pageWidth <= 0) {
          this.metrics = getPageMetrics(this.container);
        }
        const { pageWidth: pw, pageCount: pc } = this.metrics;
        if (pw <= 0) return;
        const page = Math.round(this.container.scrollLeft / pw);
        // (no-op) rAF scroll sync
        this.currentPage = Math.max(0, Math.min(page, pc - 1));
        this.update();
      });
    };
    this.container.addEventListener("scroll", this._onScroll);
  }

  /**
   * Wires up a debounced resize event listener to the window.
   * This ensures that carousel metrics and markers are re-evaluated and updated
   * only after the user has finished resizing the window, preventing excessive recalculations.
   *
   * @private
   * @pseudocode
   * 1. Define `this._onResize` as an event handler function for "resize" events:
   *    a. Clear any existing `this._resizeTimer` to debounce the resize event.
   *    b. Set `this._resizeTimer` to a new `setTimeout` that will execute after 100 milliseconds:
   *       i. Store the `oldCount` of pages from `this.metrics.pageCount`.
   *       ii. Recalculate `this.metrics` by calling `getPageMetrics` on `this.container`.
   *       iii. If the new `pageCount` is different from `oldCount`:
   *           1. Remove the existing `markersRoot` from the DOM, if it exists.
   *           2. Rebuild `markersRoot` by calling `this._buildMarkers()`.
   *           3. Append the newly built `markersRoot` to `this.wrapper`.
   *       iv. Set the current page by calling `this.setPage` with `this.currentPage`.
   * 2. Add `this._onResize` as a "resize" event listener to the `window` object.
   *
   * @returns {void}
   */
  _wireResize() {
    this._onResize = () => {
      clearTimeout(this._resizeTimer);
      this._resizeTimer = setTimeout(() => {
        const oldCount = this.metrics.pageCount;
        this.metrics = getPageMetrics(this.container);
        // Rebuild markers if page count changed
        if (this.metrics.pageCount !== oldCount) {
          this.markersRoot?.remove();
          this.markersRoot = this._buildMarkers();
          this.wrapper.append(this.markersRoot);
        }
        this.setPage(this.currentPage);
      }, 100);
    };
    window.addEventListener("resize", this._onResize);
  }

  /**
   * Builds and returns the left (previous) and right (next) navigation buttons for the carousel.
   * Each button includes an SVG icon and a text label, and is wired to navigate the carousel.
   *
   * @private
   * @pseudocode
   * 1. Create a new `button` element for the left navigation.
   * 2. Assign it the class name "scroll-button left".
   * 3. Set its `aria-label` attribute to "Prev.".
   * 4. Set its `innerHTML` to include an SVG icon for "previous" and a `span` with the text "Prev.".
   * 5. Add a "click" event listener to the left button that calls `this.prev()`.
   * 6. Create a new `button` element for the right navigation.
   * 7. Assign it the class name "scroll-button right".
   * 8. Set its `aria-label` attribute to "Next".
   * 9. Set its `innerHTML` to include an SVG icon for "next" and a `span` with the text "Next".
   * 10. Add a "click" event listener to the right button that calls `this.next()`.
   * 11. Return an object containing the created `left` and `right` button elements.
   *
   * @returns {{left: HTMLElement, right: HTMLElement}} An object containing the left and right button elements.
   */
  _buildButtons() {
    const left = document.createElement("button");
    left.className = "scroll-button left";
    left.setAttribute("aria-label", "Prev.");
    left.innerHTML =
      '<svg xmlns="http://www.w3.org/2000/svg" height="36px" width="36px" viewBox="0 -960 960 960" fill="currentColor" aria-hidden="true"><path d="M560-240 320-480l240-240 56 56-184 184 184 184-56 56Z"/></svg>' +
      '<span class="label">Prev.</span>';
    left.addEventListener("click", () => this.prev());

    const right = document.createElement("button");
    right.className = "scroll-button right";
    right.setAttribute("aria-label", "Next");
    right.innerHTML =
      '<svg xmlns="http://www.w3.org/2000/svg" height="36px" width="36px" viewBox="0 -960 960 960" fill="currentColor" aria-hidden="true"><path d="M504-480 320-664l56-56 240 240-240 240-56-56 184-184Z"/></svg>' +
      '<span class="label">Next</span>';
    right.addEventListener("click", () => this.next());

    return { left, right };
  }

  /**
   * Builds and returns the root element containing the page indicator markers and a page counter.
   * It reuses an existing markers container if found, otherwise creates a new one.
   *
   * @private
   * @pseudocode
   * 1. Attempt to find an existing `.scroll-markers` element within `this.wrapper`.
   * 2. If an existing element is found, reuse it; otherwise, create a new `div` element for the root.
   * 3. Assign the class name "scroll-markers" to the `root` element.
   * 4. Clear any existing `innerHTML` of the `root` element.
   * 5. Destructure `pageCount` from `this.metrics`.
   * 6. Loop from `i = 0` to `pageCount - 1`:
   *    a. Create a new `span` element for each `marker`.
   *    b. Assign the class name "scroll-marker" to the `marker`.
   *    c. Append the `marker` to the `root` element.
   * 7. Create a new `span` element for the `counter`.
   * 8. Assign the class name "page-counter" to the `counter`.
   * 9. Set the `aria-live` attribute of the `counter` to "polite" for accessibility.
   * 10. Append the `counter` to the `root` element.
   * 11. Return the `root` element.
   *
   * @returns {HTMLElement} The root element containing the scroll markers and page counter.
   */
  _buildMarkers() {
    // If existing markers exist (e.g., older init), reuse container
    const existing = this.wrapper.querySelector(".scroll-markers");
    const root = existing || document.createElement("div");
    root.className = "scroll-markers";
    root.innerHTML = "";

    const { pageCount } = this.metrics;
    for (let i = 0; i < pageCount; i++) {
      const marker = document.createElement("span");
      marker.className = "scroll-marker";
      root.appendChild(marker);
    }

    const counter = document.createElement("span");
    counter.className = "page-counter";
    counter.setAttribute("aria-live", "polite");
    root.appendChild(counter);

    return root;
  }

  /**
   * Synchronizes the visual state of the page indicator markers and the page counter
   * to reflect the current page of the carousel.
   *
   * @private
   * @pseudocode
   * 1. Destructure `pageCount` from `this.metrics`.
   * 2. Query all elements with the class "scroll-marker" within `this.markersRoot` to get the `markers` list.
   * 3. Iterate over each `marker` in the `markers` list along with its `index` (`i`):
   *    a. Toggle the "active" class on the `marker` based on whether its `index` (`i`) matches `this.currentPage`.
   * 4. Query the element with the class "page-counter" within `this.markersRoot` to get the `counter`.
   * 5. If `counter` exists, update its `textContent` to display the current page number (1-based) and the total `pageCount`.
   *
   * @returns {void}
   */
  _syncMarkers() {
    const { pageCount } = this.metrics;
    const markers = this.markersRoot.querySelectorAll(".scroll-marker");
    markers.forEach((m, i) => m.classList.toggle("active", i === this.currentPage));
    const counter = this.markersRoot.querySelector(".page-counter");
    if (counter) counter.textContent = `Page ${this.currentPage + 1} of ${pageCount}`;
  }
}

Generate judoka cards and append them to the container.

@param {HTMLElement} container - Carousel container element.
@param {Judoka[]} judokaList - Array of judoka objects.
@param {Object} gokyoLookup - Lookup object for gokyo data.
@returns {{ ready: Promise<void> }} Resolves when initial cards mount and listeners attach (not after images load).
1. Initialize an empty array `pendingReplacements` to track asynchronous card replacement tasks.
2. Iterate through each `judoka` object in `judokaList`:
   a. Initialize `entry` with the current `judoka`.
   b. Validate `judoka` using `hasRequiredJudokaFields()`:
      i. If validation fails, log an error with missing fields using `getMissingJudokaFields()`.
      ii. Set `entry` to a fallback judoka obtained asynchronously via `getFallbackJudoka()`.
   c. Generate a `card` element asynchronously using `generateJudokaCard(entry, gokyoLookup, container)`.
   d. If `card` generation fails (returns `null` or `undefined`):
      i. Log a warning.
      ii. Obtain another fallback judoka.
      iii. Attempt to generate a `card` again with the new fallback.
   e. If a `card` is successfully generated:
      i. Query for an `<img>` element within the `card`.
      ii. If an `img` element is found:
         1. Add an "error" event listener to the `img` element (set to run `once`).
         2. Inside the error listener, define an asynchronous `task`:
            a. Obtain a fallback judoka.
            b. Generate a `fallbackCard` without appending it to the container.
            c. If `fallbackCard` is valid and the original `card` is still in the DOM, replace the original `card` with `fallbackCard`.
            d. If the original `card` is no longer in the DOM but its parent exists, append the `fallbackCard` to the parent.
            e. Catch and log any errors during the fallback swap.
         3. Push this `task` Promise into `pendingReplacements`.
      iii. Set `card.tabIndex` to 0 to make it focusable.
      iv. Set `card.setAttribute("role", "listitem")`.
      v. Set `card.setAttribute("aria-label", card.getAttribute("data-judoka-name") || "Judoka card")` using the judoka's name or a generic label.
      vi. Append the `card` to the `container`.
3. After the loop, define an asynchronous `ready` task that:
   a. Yields control to the event loop using `await Promise.resolve()` so synchronously dispatched image error events can register their tasks.
   b. Awaits all tasks in `pendingReplacements` with `Promise.allSettled()`.
4. Return an object containing the `ready` Promise.

export function appendCards(container, judokaList, gokyoLookup) {
  // Track any in-flight replacements triggered during this execution.
  const pendingReplacements = [];

  const ready = (async () => {
    for (const judoka of judokaList) {
      let entry = judoka;
      if (!hasRequiredJudokaFields(judoka)) {
        console.error("Invalid judoka object:", judoka);
        const missing = getMissingJudokaFields(judoka).join(", ");
        console.error(`Missing fields: ${missing}`);
        entry = await getFallbackJudoka();
      }
      let card = await generateJudokaCard(entry, gokyoLookup, container);
      if (!card) {
        console.warn("Failed to generate card for judoka:", entry);
        const fallback = await getFallbackJudoka();
        card = await generateJudokaCard(fallback, gokyoLookup, container);
      }
      if (card) {
        const img = card.querySelector("img");
        if (img) {
          // Handle portrait load errors by swapping to a fallback card, but
          // do not block on image load events while building the carousel.
          img.addEventListener(
            "error",
            () => {
              // Start the replacement asynchronously and track it so that
              // appendCards can await if the error occurs before it returns.
              const task = (async () => {
                try {
                  const fallback = await getFallbackJudoka();
                  // Build a replacement card without auto-appending to container.
                  const fallbackCard = await generateJudokaCard(fallback, gokyoLookup);
                  if (fallbackCard) {
                    const parent = container || card.parentElement;
                    if (parent && parent.contains(card)) {
                      parent.replaceChild(fallbackCard, card);
                    } else if (parent) {
                      parent.appendChild(fallbackCard);
                    }
                  }
                } catch (err) {
                  console.error("Failed to swap to fallback card", err);
                }
              })();
              pendingReplacements.push(task);
            },
            { once: true }
          );
        }
        card.tabIndex = 0;
        card.setAttribute("role", "listitem");
        card.setAttribute("aria-label", card.getAttribute("data-judoka-name") || "Judoka card");
        container.appendChild(card);
      }
    }
    // Give the event loop a turn so any synchronously dispatched image
    // error events can register tasks, then await them.
    await Promise.resolve();
    if (pendingReplacements.length) {
      await Promise.allSettled(pendingReplacements);
    }
  })();

  return { ready };
}

